let myImg=document.querySelector("#myImg");window.addEventListener("resize",function(){900<=window.innerWidth?myImg.src="../img/planet-1440.svg":myImg.src="../img/planet.svg"});const inputElement=document.querySelector("#block1-input");function updatePlaceholderText(){780<=window.innerWidth?inputElement.placeholder="Введите email чтобы войти в аккаунт или создать новый":inputElement.placeholder="Введите email "}updatePlaceholderText(),window.addEventListener("resize",updatePlaceholderText);let button=document.querySelector("#block1-button");button.addEventListener("click",e=>{""!==inputElement.value.trim()&&inputElement.value.includes("@")?(inputElement.classList.remove("red-border"),inputElement.placeholder=""):(inputElement.classList.add("red-border"),inputElement.placeholder="ошибка",e.preventDefault())});